mapper.select:
SELECT m.mentor_id AS mentorId, u.user_name AS userName, u.nickname, u.profile_image_url AS profileImageUrl, m.price, m.temperature, ts.techstack_name AS techstackName FROM mentor m JOIN user u ON m.user_id = u.user_id LEFT JOIN user_and_techstack ut ON u.user_id = ut.user_id LEFT JOIN techstack ts ON ut.techstack_id = ts.techstack_id
AND ts.techstack_name = #{techstackName}
AND u.user_name LIKE CONCAT('%', #{userName}, '%')
ORDER BY m.price ${coffeechatPrice}
ORDER BY m.temperature ${temperature}
ORDER BY m.created_at DESC
LIMIT #{size} OFFSET #{page}
